CREATE TABLE `api_configurations` (
  `api_configuration_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `path` varchar(100) COLLATE utf8mb4_bin DEFAULT NULL,
  `request_type` ENUM('object', 'string', 'form') COLLATE utf8mb4_bin NOT NULL COMMENT 'This is the type of the request for parsing',
  `request` text,
  `request_options` text,
  `error_response` text,
  `success_response` text,
  `response_options` text,
  `skip_validation` tinyint(1) DEFAULT '0',
  `type` ENUM('incoming', 'outgoing') COLLATE utf8mb4_bin DEFAULT NULL,
  `status` varchar(100) COLLATE utf8mb4_bin DEFAULT NULL,
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`api_configuration_id`)
) ENGINE=InnoDB DEFAULT AUTO_INCREMENT=1 CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;



